import React from 'react';
import { marked } from 'marked';

function convertStrong(text) {
  return text.replace(/\*\*(.+?)\*\*/g, '<strong>$1</strong>');
}

function ArticleDetail({ article, analysis, loading }) {
  return (
    <div style={{ marginTop: 20 }}>
      <h2>{article.title}</h2>
      <p><strong>Company:</strong> {article.company}</p>
      <a href={article.url} target="_blank" rel="noopener noreferrer">View Article</a>
      <h3>Analysis</h3>
      {loading ? (
        <p>Loading analysis...</p>
      ) : (
        <div
          style={{
            whiteSpace: 'pre-wrap',
            backgroundColor: '#f0f0f0',
            padding: 10,
            borderRadius: 5,
          }}
          dangerouslySetInnerHTML={{
            __html: marked.parse(convertStrong(analysis || 'No analysis available'), { breaks: true })
          }}
        />
      )}
    </div>
  );
}

export default ArticleDetail;
