import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';

function ArticleDetail({ article, analysis, loading }) {
  return (
    <div style={{ marginTop: 20 }}>
      <h2 style={{ color: '#0d47a1', fontFamily: 'Arial, sans-serif' }}>{article.title}</h2>
      <p style={{ fontFamily: 'Arial, sans-serif' }}>
        <strong>Company:</strong> {article.company}
      </p>
      <a
        href={article.url}
        target="_blank"
        rel="noopener noreferrer"
        style={{ color: '#1976d2', textDecoration: 'underline', fontFamily: 'Arial, sans-serif' }}
      >
        View Article
      </a>

      <h3 style={{ marginTop: 20, color: '#0d47a1', fontFamily: 'Arial, sans-serif' }}>Analysis</h3>
      {loading ? (
        <p style={{ fontFamily: 'Arial, sans-serif' }}>Loading analysis...</p>
      ) : (
        <div
          style={{
            marginTop: 10,
            background: `rgba(13, 71, 161, 0.15)`,
            padding: 15,
            borderRadius: 8,
            border: '1px solid #90caf9',
            color: '#0d47a1',
            fontFamily: 'Arial, sans-serif',
            lineHeight: 1.6,
            whiteSpace: 'pre-wrap',
            backgroundBlendMode: 'lighten',
            boxShadow: '0 2px 6px rgba(0,0,0,0.05)',
            backdropFilter: 'blur(2px)',
          }}
        >
          <ReactMarkdown remarkPlugins={[remarkGfm]}>
            {analysis || 'No analysis available'}
          </ReactMarkdown>
        </div>
      )}
    </div>
  );
}

export default ArticleDetail;
