import React from 'react';
import { marked } from 'marked';

// Hàm loại bỏ các dấu * và ** khỏi nội dung markdown
function removeMarkdownBold(text) {
  return text.replace(/\*{1,2}(.*?)\*{1,2}/g, '$1');
}

function ArticleDetail({ article, analysis, loading }) {
  return (
    <div style={{ marginTop: 20 }}>
      <h2>{article.title}</h2>
      <p><strong>Company:</strong> {article.company}</p>
      <a href={article.url} target="_blank" rel="noopener noreferrer">View Article</a>
      <h3>Analysis</h3>
      {loading ? (
        <p>Loading analysis...</p>
      ) : (
        <div
          style={{
            whiteSpace: 'pre-wrap',
            backgroundColor: '#f0f0f0',
            padding: 10,
            borderRadius: 5,
          }}
          
        />
      )}
    </div>
  );
}

export default ArticleDetail;
