import React, { useState, useEffect } from 'react';
import ArticleList from './components/ArticleList';
import ArticleDetail from './components/ArticleDetail';

// Danh sách công ty gợi ý
const companyList = [
  'Temasek', 'Sembcorp', 'Grab', 'BlackRock', 'Tesla', 'Apple', 'Microsoft'
];

function App() {
  const [company, setCompany] = useState('');
  const [articles, setArticles] = useState([]);
  const [selectedArticle, setSelectedArticle] = useState(null);
  const [analysis, setAnalysis] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [history, setHistory] = useState([]);

  // Load history from localStorage
  useEffect(() => {
    const saved = localStorage.getItem('searchHistory');
    if (saved) setHistory(JSON.parse(saved));
  }, []);

  // Lưu lịch sử tìm kiếm
  const saveHistory = (term) => {
    const updated = [...new Set([term, ...history])].slice(0, 5);
    setHistory(updated);
    localStorage.setItem('searchHistory', JSON.stringify(updated));
  };

  // Speech-to-text logic
  const handleSpeech = () => {
    const recognition = new window.webkitSpeechRecognition();
    recognition.lang = 'en-US';
    recognition.start();

    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      const names = transcript
        .split(/,| and | & /i)
        .map(n => n.trim())
        .filter(n => n.length > 0);
      setCompany(names.join(', '));
    };

    recognition.onerror = (event) => {
      console.error('Speech error:', event.error);
    };
  };

  // Gửi truy vấn tìm kiếm
  const handleSearch = () => {
    const names = company
      .split(/,| and | & /i)
      .map(n => n.trim())
      .filter(n => n.length > 0);
    if (names.length === 0) return;

    setLoading(true);
    setArticles([]);
    setSelectedArticle(null);
    setError('');

    fetch('/api/analyze_companies', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ companies: names })
    })
      .then(res => res.json())
      .then(data => {
        const allArticles = [];
        data.forEach(companyData => {
          companyData.articles.forEach(article => {
            allArticles.push({
              ...article,
              company: companyData.company
            });
          });
        });
        if (allArticles.length === 0) {
          setError('No articles found.');
        }
        setArticles(allArticles);
        saveHistory(company);
      })
      .catch(() => {
        setError('Error connecting to backend.');
      })
      .finally(() => setLoading(false));
  };

  const handleSelectArticle = (article) => {
    setSelectedArticle(article);
    setAnalysis(article.analysis || 'No analysis available');
  };

  return (
    <div style={{ maxWidth: 800, margin: 'auto', padding: 20 }}>
      <h1>ESG News Monitor</h1>

      {/* Input + Speech button */}
      <div style={{ marginBottom: 10 }}>
        <input
          value={company}
          onChange={(e) => setCompany(e.target.value)}
          onKeyDown={(e) => {
            if (e.key === 'Enter') handleSearch();
          }}
          placeholder="Enter company names (e.g., Temasek, Sembcorp)"
          style={{ padding: 8, width: 300, marginRight: 10 }}
        />

        <button onClick={handleSearch} style={{ padding: 8 }}>Search</button>
        <button onClick={handleSpeech} style={{ padding: 8, marginLeft: 5 }}>🎤 Speak</button>
      </div>

      {/* Autocomplete */}
      {company && (
        <ul style={{
          background: '#f9f9f9', listStyle: 'none',
          padding: 5, border: '1px solid #ccc', maxWidth: 300
        }}>
          {companyList
            .filter(c => c.toLowerCase().includes(company.toLowerCase()) && !company.includes(c))
            .map((c, i) => (
              <li key={i}
                onClick={() => setCompany(company ? company + ', ' + c : c)}
                style={{ cursor: 'pointer', padding: 5 }}>
                {c}
              </li>
            ))}
        </ul>
      )}

      {/* History */}
      {history.length > 0 && (
        <div style={{ marginTop: 10 }}>
          <strong>Search History:</strong>
          <ul style={{ paddingLeft: 15 }}>
            {history.map((h, i) => (
              <li key={i} style={{ cursor: 'pointer' }} onClick={() => setCompany(h)}>
                {h}
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Results */}
      {loading && <p>Loading...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <ArticleList articles={articles} onSelect={handleSelectArticle} />
      {selectedArticle && (
        <ArticleDetail article={selectedArticle} analysis={analysis} />
      )}
    </div>
  );
}

export default App;
