import React from 'react';

function ArticleDetail({ article, analysis, loading }) {
  return (
    <div>
      <h2>{article.title}</h2>
      <a href={article.url} target="_blank" rel="noreferrer">View Article</a>
      <p><strong>Company:</strong> {article.company}</p>
      <h3>Analysis</h3>
      {loading ? <p>Loading...</p> : <pre>{analysis}</pre>}
    </div>
  );
}

export default ArticleDetail;
