import React from 'react';

function ArticleDetail({ article, analysis, loading }) {
  return (
    <div style={{ marginTop: 20 }}>
      <h2>{article.title}</h2>
      <p><strong>Company:</strong> {article.company}</p>
      <a href={article.url} target="_blank" rel="noopener noreferrer">View Article</a>
      <h3>Analysis</h3>
      {loading ? (
        <p>Loading analysis...</p>
      ) : (
        <div style={{ whiteSpace: 'pre-wrap', backgroundColor: '#f0f0f0', padding: 10, borderRadius: 5 }}>
          {analysis
            ? analysis.split('\n').map((line, idx) => {
                const match = line.match(/^(\w[\w\s]+):\s*(.*)/);  // match "Summary:", "ESG Risk:", etc.
                if (match) {
                  return (
                    <p key={idx}>
                      <strong>{match[1]}:</strong> {match[2]}
                    </p>
                  );
                }
                return <p key={idx}>{line}</p>;
              })
            : 'No analysis available'}
        </div>
      )}
    </div>
  );
}

export default ArticleDetail;
