import React, { useState, useEffect } from 'react';
import ArticleList from './components/ArticleList';
import ArticleDetail from './components/ArticleDetail';

function App() {
  const [articles, setArticles] = useState([]);
  const [selectedArticle, setSelectedArticle] = useState(null);
  const [analysis, setAnalysis] = useState('');
  const [loading, setLoading] = useState(false);

  // Load ESG articles from backend
  useEffect(() => {
    fetch('/api/analyze_companies', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ companies: ['Temasek', 'Sembcorp'] }) // có thể thay bằng company_list.txt nội dung tương tự
    })
      .then(res => res.json())
      .then(data => {
        const allArticles = [];
        for (const company in data) {
          data[company].forEach(article => {
            if (article.title) {
              allArticles.push({
                ...article,
                company
              });
            }
          });
        }
        setArticles(allArticles);
      })
      .catch(err => {
        console.error('Error fetching ESG data:', err);
      });
  }, []);

  const handleSelectArticle = (article) => {
    setSelectedArticle(article);
    setAnalysis(article.analysis || 'No analysis available');
  };

  return (
    <div style={{ maxWidth: 800, margin: 'auto', padding: 20 }}>
      <h1>ESG News Monitor</h1>
      <ArticleList articles={articles} onSelect={handleSelectArticle} />
      {selectedArticle && (
        <ArticleDetail
          article={selectedArticle}
          analysis={analysis}
          loading={loading}
        />
      )}
    </div>
  );
}

export default App;
