import React, { useState } from 'react';

function ArticleList({ articles, onSelect }) {
  return (
    <div style={{ marginBottom: 20 }}>
      <h2>Articles</h2>
      {articles.length === 0 && <p>No articles found.</p>}
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {articles.map((article, idx) => (
          <li
            key={idx}
            onClick={() => onSelect(article)}
            style={{
              cursor: 'pointer',
              padding: 10,
              borderBottom: '1px solid #ccc'
            }}
          >
            <strong>{article.title}</strong> <br />
            <small>Company: {article.company}</small>
          </li>
        ))}
      </ul>
    </div>
  );
}

function ArticleDetail({ article, analysis }) {
  if (!article) return null;

  return (
    <div style={{ borderTop: '1px solid #ccc', paddingTop: 20 }}>
      <h2>Article Detail</h2>
      <h3>{article.title}</h3>
      <p><strong>Company:</strong> {article.company}</p>
      <p>
        <a href={article.url} target="_blank" rel="noreferrer">
          Read Full Article
        </a>
      </p>
      <h4>ESG Analysis</h4>
      <pre
        style={{
          whiteSpace: 'pre-wrap',
          background: '#f7f7f7',
          padding: 10,
          borderRadius: 5,
          maxHeight: 300,
          overflowY: 'auto'
        }}
      >
        {analysis || 'No analysis available'}
      </pre>
    </div>
  );
}

function App() {
  const [company, setCompany] = useState('');
  const [articles, setArticles] = useState([]);
  const [selectedArticle, setSelectedArticle] = useState(null);
  const [analysis, setAnalysis] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSearch = () => {
    if (!company.trim()) return;
    setLoading(true);
    setArticles([]);
    setSelectedArticle(null);
    setError('');

    fetch('/api/analyze_companies', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ companies: [company.trim()] })
    })
      .then(res => res.json())
      .then(data => {
        const allArticles = [];
        data.forEach(companyData => {
          companyData.articles.forEach(article => {
            allArticles.push({
              ...article,
              company: companyData.company
            });
          });
        });
        if (allArticles.length === 0) {
          setError('No articles found.');
        }
        setArticles(allArticles);
      })
      .catch(err => {
        console.error('Error fetching ESG data:', err);
        setError('Error connecting to backend.');
      })
      .finally(() => setLoading(false));
  };

  const handleVoiceSearch = () => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert("Speech Recognition not supported in this browser.");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = 'en-US';
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;

    recognition.onresult = (event) => {
      const spokenText = event.results[0][0].transcript;
      setCompany(spokenText);
      setTimeout(() => {
        handleSearch();
      }, 300);
    };

    recognition.onerror = (event) => {
      console.error('Speech recognition error:', event.error);
      alert('Error during speech recognition.');
    };

    recognition.start();
  };

  const handleSelectArticle = (article) => {
    setSelectedArticle(article);
    setAnalysis(article.analysis || 'No analysis available');
  };

  return (
    <div style={{ maxWidth: 800, margin: 'auto', padding: 20 }}>
      <h1>ESG News Monitor</h1>

      <div style={{ marginBottom: 20 }}>
        <input
          value={company}
          onChange={(e) => setCompany(e.target.value)}
          placeholder="Enter company name (e.g., Temasek)"
          style={{ padding: 8, width: 300, marginRight: 10 }}
        />
        <button onClick={handleSearch} style={{ padding: 8 }}>
          Search
        </button>
        <button onClick={handleVoiceSearch} style={{ padding: 8, marginLeft: 10 }}>
          🎤 Speak
        </button>
      </div>

      {loading && <p>Loading...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <ArticleList articles={articles} onSelect={handleSelectArticle} />
      {selectedArticle && (
        <ArticleDetail article={selectedArticle} analysis={analysis} />
      )}
    </div>
  );
}

export default App;
