import React, { useEffect, useState } from 'react';

function EsgInfo({ symbol }) {
  const [esgData, setEsgData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    if (!symbol) return;

    setLoading(true);
    setError('');
    setEsgData(null);

    fetch('http://localhost:8000/api/finnhub_esg', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ symbol }),
    })
      .then(res => res.json())
      .then(data => setEsgData(data))
      .catch(() => setError('Failed to fetch ESG data'))
      .finally(() => setLoading(false));
  }, [symbol]);

  if (loading) return <p>Loading ESG data...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;
  if (!esgData) return null;

  return (
    <div style={{ backgroundColor: '#eef6ff', padding: 10, borderRadius: 5, marginTop: 10 }}>
      <h4>ESG Scores for {symbol}</h4>
      <ul>
        <li><strong>Environmental Score:</strong> {esgData.environmentalScore ?? 'N/A'}</li>
        <li><strong>Social Score:</strong> {esgData.socialScore ?? 'N/A'}</li>
        <li><strong>Governance Score:</strong> {esgData.governanceScore ?? 'N/A'}</li>
        <li><strong>Overall ESG Score:</strong> {esgData.esgScore ?? 'N/A'}</li>
      </ul>
    </div>
  );
}

export default EsgInfo;
