import React, { useState, useEffect, useRef } from 'react';
import ArticleList from './components/ArticleList';
import ArticleDetail from './components/ArticleDetail';
import ReactMarkdown from 'react-markdown';
import EsgInfo from './components/EsgInfo';

const API_BASE =
  window.location.hostname === 'localhost' || window.location.hostname === '127.0.0.1'
    ? 'http://localhost:8000'
    : 'https://esg-monitor-agent.onrender.com';

const companyToTicker = {
  Tesla: 'TSLA',
  'First Solar': 'FSLR',
  'Brookfield Renewable': 'BEP',
  'NextEra Energy': 'NEE',
  Vestas: 'VWS',
  Orsted: 'ORSTED.CO',
  'Enphase Energy': 'ENPH',
  'ATA Creativity Global': 'AACG',
};

const companyList = Object.keys(companyToTicker);

function App() {
  const [input, setInput] = useState('');
  const [articles, setArticles] = useState([]);
  const [selectedArticle, setSelectedArticle] = useState(null);
  const [analysis, setAnalysis] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [history, setHistory] = useState([]);
  const [summaries, setSummaries] = useState({});
  const [esgData, setEsgData] = useState({});
  const [isQuestion, setIsQuestion] = useState(false);
  const [isRecording, setIsRecording] = useState(false);

  const inputRef = useRef(null);

  useEffect(() => {
    const saved = localStorage.getItem('searchHistory');
    if (saved) setHistory(JSON.parse(saved));
  }, []);

  const saveHistory = (term) => {
    setHistory(prevHistory => {
      const updated = [...new Set([term, ...prevHistory])].slice(0, 5);
      localStorage.setItem('searchHistory', JSON.stringify(updated));
      return updated;
    });
  };

  const handleSpeech = () => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      setError('Speech recognition not supported in this browser.');
      return;
    }
    const recognition = new SpeechRecognition();
    recognition.lang = 'en-US';

    recognition.onstart = () => {
      setIsRecording(true);
      setError('');
    };

    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setInput(transcript);
    };

    recognition.onerror = (event) => {
      console.error('Speech error:', event.error);
      setError('Speech recognition failed.');
      setIsRecording(false);
    };

    recognition.onend = () => {
      setIsRecording(false);
    };

    recognition.start();
  };

  const handleSearch = () => {
    if (!input.trim()) return;

    setLoading(true);
    setArticles([]);
    setSelectedArticle(null);
    setError('');
    setSummaries({});
    setEsgData({});
    setIsQuestion(false);

    const lowerInput = input.toLowerCase();
    const questionCheck = lowerInput.includes('which') || lowerInput.includes('what') || lowerInput.includes('in the');
    setIsQuestion(questionCheck);

    if (questionCheck) {
      fetch(`${API_BASE}/api/search_query`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ question: input }),
      })
        .then(res => res.json())
        .then(data => {
          if (data.error) {
            setError(data.error);
          } else {
            const company = data.company || input;
            setEsgData({ [company]: data });
            setSummaries({
              [company]: `ESG Scores: Environmental ${data.environment_score ?? 'N/A'}, Social ${data.social_score ?? 'N/A'}, Governance ${data.governance_score ?? 'N/A'}, Total ${data.total_score ?? 'N/A'}`,
            });
            fetch(`${API_BASE}/api/analyze_companies`, {
              method: 'POST',
              headers: { 'Content-Type': 'application/json' },
              body: JSON.stringify({ companies: [company] }),
            })
              .then(res => res.json())
              .then(companyData => {
                const allArticles = [];
                companyData.forEach(c => {
                  c.articles.forEach(article => {
                    allArticles.push({ ...article, company: c.company });
                  });
                });
                setArticles(allArticles);
              })
              .catch(() => setError('Failed to fetch articles.'));
          }
          saveHistory(input);
        })
        .catch(() => {
          setError('Error connecting to backend.');
        })
        .finally(() => setLoading(false));
    } else {
      const names = input
        .split(/,| and | & /i)
        .map(n => n.trim())
        .filter(n => n.length > 0);

      if (names.length === 0) {
        setError('Please enter valid company names or a query.');
        setLoading(false);
        return;
      }

      fetch(`${API_BASE}/api/analyze_companies`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ companies: names }),
      })
        .then(res => res.json())
        .then(data => {
          const allArticles = [];
          const newSummaries = {};
          const newEsgData = {};

          data.forEach(companyData => {
            companyData.articles.forEach(article => {
              allArticles.push({ ...article, company: companyData.company });
            });

            newSummaries[companyData.company] = companyData.overall_summary;

            if (companyData.esg) {
              newEsgData[companyData.company] = companyData.esg;
            }
          });

          if (allArticles.length === 0) {
            setError('No articles found.');
          }

          setArticles(allArticles);
          setSummaries(newSummaries);
          setEsgData(newEsgData);
          saveHistory(input);
        })
        .catch(() => {
          setError('Error connecting to backend.');
        })
        .finally(() => setLoading(false));
    }
  };

  const handleSelectArticle = (article) => {
    setSelectedArticle(article);
    setAnalysis(article.analysis || 'No analysis available');
  };

  const handleHistoryClick = (term) => {
    setInput(term);
    setTimeout(() => {
      if (inputRef.current) inputRef.current.focus();
    }, 10);
  };

  const handleInputKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  return (
    <div style={{ maxWidth: 800, margin: 'auto', padding: 20, fontFamily: 'Montserrat, sans-serif', position: 'relative' }}>
      {isRecording && (
        <div style={{
          position: 'fixed',
          top: 0, left: 0, right: 0, bottom: 0,
          backgroundColor: 'rgba(0,0,0,0.4)',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          zIndex: 9999,
        }}>
          <div style={{
            width: 150,
            height: 150,
            backgroundColor: '#0d47a1',
            borderRadius: '50%',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            boxShadow: '0 0 20px 5px #2196f3',
          }}>
            <svg xmlns="http://www.w3.org/2000/svg" fill="white" viewBox="0 0 24 24" width="80" height="80">
              <path d="M3 10v4h4l5 5V5L7 10H3z"/>
              <path d="M16.5 12a4.5 4.5 0 0 1-1.24 3.18l1.43 1.43A6 6 0 0 0 18 12a6 6 0 0 0-1.31-3.76l-1.43 1.43A4.5 4.5 0 0 1 16.5 12z"/>
              <path d="M14 3.23v2.06a7.98 7.98 0 0 1 3.56 3.56h2.06a9.98 9.98 0 0 0-5.62-5.62z"/>
            </svg>
          </div>
        </div>
      )}

      <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 20 }}>
        <h1 style={{ color: '#0d47a1' }}>ESG News Monitor</h1>
        <div style={{ display: 'flex', alignItems: 'center', gap: 16 }}>
          <img src="https://www.nus.edu.sg/images/default-source/identity-images/NUS_logo_full-horizontal.jpg" alt="NUS Logo" style={{ height: 120 }} />
          <img src="https://ohh12.hcmut.edu.vn/img/news/RQ6hG8A_M89fTWtFX8YYwNPJ.jpg" alt="HUS Logo" style={{ height: 72, borderRadius: 8 }} />
        </div>
      </header>

      <div style={{ marginBottom: 10, display: 'flex', gap: 10 }}>
        <input
          ref={inputRef}
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleInputKeyDown}
          placeholder="Enter company names or a query (e.g., Which is the ESG score of ATA Creativity Global?)"
          style={{
            padding: 10,
            width: '100%',
            maxWidth: 600,
            borderRadius: 8,
            border: '1px solid #2196f3',
            outlineColor: '#1976d2',
            fontSize: 16,
            fontFamily: 'Montserrat, sans-serif',
            color: input.trim() ? '#0d47a1' : '#555',
            transition: 'color 0.3s',
          }}
        />
        <button
          onClick={handleSearch}
          style={{
            padding: '10px 15px',
            borderRadius: 20,
            backgroundColor: '#2196f3',
            border: 'none',
            color: 'white',
            fontWeight: 'bold',
            fontSize: 16,
            fontFamily: 'Montserrat, sans-serif',
            cursor: 'pointer',
            transition: 'background-color 0.3s',
          }}
          onMouseEnter={e => e.currentTarget.style.backgroundColor = '#1976d2'}
          onMouseLeave={e => e.currentTarget.style.backgroundColor = '#2196f3'}
        >
          Search
        </button>
        <button
          onClick={handleSpeech}
          disabled={isRecording}
          style={{
            padding: '14px 20px',
            borderRadius: 18,
            backgroundColor: isRecording ? '#a0cfff' : '#64b5f6',
            border: 'none',
            color: 'white',
            fontWeight: 'bold',
            fontSize: 16,
            fontFamily: 'Montserrat, sans-serif',
            cursor: isRecording ? 'not-allowed' : 'pointer',
            transition: 'background-color 0.3s',
          }}
          onMouseEnter={e => {
            if (!isRecording) e.currentTarget.style.backgroundColor = '#42a5f5';
          }}
          onMouseLeave={e => {
            if (!isRecording) e.currentTarget.style.backgroundColor = '#64b5f6';
          }}
        >
          ðŸŽ¤ Speak
        </button>
      </div>

      {history.length > 0 && (
        <div style={{ marginTop: 10 }}>
          <strong style={{ color: '#0d47a1' }}>Search History:</strong>
          <ul style={{ paddingLeft: 15, fontFamily: 'Montserrat, sans-serif', fontSize: 18, color: '#0d47a1' }}>
            {history.map((h, i) => (
              <li
                key={i}
                style={{
                  cursor: 'pointer',
                  padding: 4,
                  borderRadius: 5,
                }}
                tabIndex={0}
                onClick={() => handleHistoryClick(h)}
                onKeyDown={e => {
                  if (e.key === 'Enter') {
                    handleHistoryClick(h);
                    handleSearch();
                  }
                }}
                onMouseEnter={e => e.currentTarget.style.backgroundColor = '#bbdefb'}
                onMouseLeave={e => e.currentTarget.style.backgroundColor = 'transparent'}
              >
                {h}
              </li>
            ))}
          </ul>
        </div>
      )}

      {loading && <p style={{ color: '#0d47a1' }}>Loading...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {Object.entries(summaries).length > 0 && (
        <div style={{ marginTop: 20, background: '#e3f2fd', padding: 15, borderRadius: 8, border: '1px solid #90caf9' }}>
          <h2 style={{ color: '#0d47a1' }}>Company Summaries</h2>
          {Object.entries(summaries).map(([company, summary], idx) => (
            <div
              key={company}
              style={{
                marginBottom: 20,
                background: `rgba(13, 71, 161, ${(0.1 + idx * 0.15).toFixed(2)})`,
                padding: 15,
                borderRadius: 8,
                color: '#0d47a1',
                fontFamily: 'Montserrat, sans-serif',
                lineHeight: 1.6,
                boxShadow: '0 2px 6px rgba(0,0,0,0.05)',
              }}
            >
              <h3>{company}</h3>
              <EsgInfo
                symbol={companyToTicker[company] || company}
                esgData={esgData[company]}
              />
              <h4>Investment Recommendation</h4>
              <ReactMarkdown>{summary}</ReactMarkdown>
            </div>
          ))}
        </div>
      )}

      <ArticleList articles={articles} onSelect={handleSelectArticle} />
      {selectedArticle && <ArticleDetail article={selectedArticle} analysis={analysis} />}

      <footer
        style={{
          marginTop: 40,
          paddingTop: 20,
          borderTop: '1px solid #90caf9',
          textAlign: 'center',
          color: '#0d47a1',
          fontSize: 14,
          fontFamily: 'Montserrat, sans-serif',
        }}
      >
        Developed by <strong>Nguyen Thi Thu Hue</strong> VNU University of Science, 2025
      </footer>
    </div>
  );
}

export default App;
