import React, { useState, useEffect } from 'react';
import ArticleList from './components/ArticleList';
import ArticleDetail from './components/ArticleDetail';

// Danh sách công ty mẫu
const companyList = [
  'Tesla',
  'First Solar',
  'Brookfield Renewable',
  'NextEra Energy',
  'Vestas',
  'Orsted',
  'Enphase Energy'
];

function App() {
  const [input, setInput] = useState('');
  const [articles, setArticles] = useState([]);
  const [selectedArticle, setSelectedArticle] = useState(null);
  const [analysis, setAnalysis] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [history, setHistory] = useState([]);
  const [summaries, setSummaries] = useState({});
  const [suggestedCompanies, setSuggestedCompanies] = useState([]);

  useEffect(() => {
    const saved = localStorage.getItem('searchHistory');
    if (saved) setHistory(JSON.parse(saved));
  }, []);

  const saveHistory = (term) => {
    const updated = [...new Set([term, ...history])].slice(0, 5);
    setHistory(updated);
    localStorage.setItem('searchHistory', JSON.stringify(updated));
  };

  const handleSpeech = () => {
    const recognition = new window.webkitSpeechRecognition();
    recognition.lang = 'en-US';
    recognition.start();

    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setInput(transcript);
    };

    recognition.onerror = (event) => {
      console.error('Speech error:', event.error);
      setError('Speech recognition failed.');
    };
  };

  const handleSearch = () => {
    if (!input.trim()) return;

    setLoading(true);
    setArticles([]);
    setSelectedArticle(null);
    setError('');
    setSummaries({});
    setSuggestedCompanies([]);

    const isQuestion = input.toLowerCase().includes('which') || input.toLowerCase().includes('what') || input.toLowerCase().includes('in the');
    const keywords = input.toLowerCase().includes('clean energy') ? ['clean energy'] : [];

    if (isQuestion && keywords.length > 0) {
      fetch('/api/search_query', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ query: input, keywords })
      })
        .then(res => res.json())
        .then(data => {
          if (data.companies && data.companies.length > 0) {
            setSuggestedCompanies(data.companies);
            setSummaries(data.summaries || {});
            setArticles(data.articles || []);
          } else {
            setError('No relevant companies or articles found.');
          }
          saveHistory(input);
        })
        .catch(() => {
          setError('Error connecting to backend.');
        })
        .finally(() => setLoading(false));
    } else {
      const names = input
        .split(/,| and | & /i)
        .map(n => n.trim())
        .filter(n => n.length > 0);

      if (names.length === 0) {
        setError('Please enter valid company names or a query.');
        setLoading(false);
        return;
      }

      fetch('/api/analyze_companies', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ companies: names })
      })
        .then(res => res.json())
        .then(data => {
          const allArticles = [];
          const newSummaries = {};

          data.forEach(companyData => {
            companyData.articles.forEach(article => {
              allArticles.push({
                ...article,
                company: companyData.company
              });
            });
            newSummaries[companyData.company] = companyData.overall_summary;
          });

          if (allArticles.length === 0) {
            setError('No articles found.');
          }

          setArticles(allArticles);
          setSummaries(newSummaries);
          saveHistory(input);
        })
        .catch(() => {
          setError('Error connecting to backend.');
        })
        .finally(() => setLoading(false));
    }
  };

  const handleSelectArticle = (article) => {
    setSelectedArticle(article);
    setAnalysis(article.analysis || 'No analysis available');
  };

  return (
    <div style={{ maxWidth: 800, margin: 'auto', padding: 20 }}>
      <h1>ESG News Monitor</h1>

      <div style={{ marginBottom: 10 }}>
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => {
            if (e.key === 'Enter') handleSearch();
          }}
          placeholder="Enter company names or a query (e.g., Which companies in the clean energy sector should I invest in?)"
          style={{ padding: 8, width: 400, marginRight: 10 }}
        />
        <button onClick={handleSearch} style={{ padding: 8 }}>Search</button>
        <button onClick={handleSpeech} style={{ padding: 8, marginLeft: 5 }}>🎤 Speak</button>
      </div>

      {/* Autocomplete */}
      {input && !input.toLowerCase().includes('which') && !input.toLowerCase().includes('what') && (
        <ul style={{
          background: '#f9f9f9', listStyle: 'none',
          padding: 5, border: '1px solid #ccc', maxWidth: 400
        }}>
          {companyList
            .filter(c => c.toLowerCase().includes(input.toLowerCase()) && !input.includes(c))
            .map((c, i) => (
              <li key={i}
                onClick={() => setInput(input ? input + ', ' + c : c)}
                style={{ cursor: 'pointer', padding: 5 }}>
                {c}
              </li>
            ))}
        </ul>
      )}

      {/* Search history */}
      {history.length > 0 && (
        <div style={{ marginTop: 10 }}>
          <strong>Search History:</strong>
          <ul style={{ paddingLeft: 15 }}>
            {history.map((h, i) => (
              <li key={i} style={{ cursor: 'pointer' }} onClick={() => setInput(h)}>
                {h}
              </li>
            ))}
          </ul>
        </div>
      )}

      {loading && <p>Loading...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Suggested Companies */}
      {suggestedCompanies.length > 0 && (
        <div style={{ marginTop: 20, background: '#e6f3ff', padding: 10, borderRadius: 5 }}>
          <h2>Suggested Companies in Clean Energy Sector</h2>
          <ul style={{ paddingLeft: 15 }}>
            {suggestedCompanies.map((company, i) => (
              <li key={i} style={{ marginBottom: 10 }}>
                <strong>{company.name}</strong>: {company.description}
                {summaries[company.name] && (
                  <div>
                    <h4>Investment Recommendation</h4>
                    <pre style={{ whiteSpace: 'pre-wrap' }}>{summaries[company.name]}</pre>
                  </div>
                )}
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Company Summaries */}
      {Object.entries(summaries).length > 0 && suggestedCompanies.length === 0 && (
  <div style={{ marginTop: 20, background: '#f5f5f5', padding: 10, borderRadius: 5 }}>
    <h2>Company Summaries</h2>
    {Object.entries(summaries).map(([company, summary]) => (
      <div key={company} style={{ marginBottom: 10 }}>
        <h3>{company}</h3>
        <h4>Investment Recommendation</h4>
        <ReactMarkdown>{summary}</ReactMarkdown>
      </div>
    ))}
  </div>
)}

      <ArticleList articles={articles} onSelect={handleSelectArticle} />
      {selectedArticle && (
        <ArticleDetail article={selectedArticle} analysis={analysis} />
      )}
    </div>
  );
}

export default App;
