import React, { useState, useEffect } from 'react';

function ArticleList({ articles, onSelect }) {
  return (
    <div style={{ marginBottom: 20 }}>
      <h2>Articles</h2>
      {articles.length === 0 && <p>No articles found.</p>}
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {articles.map((article, idx) => (
          <li
            key={idx}
            onClick={() => onSelect(article)}
            style={{
              cursor: 'pointer',
              padding: 10,
              borderBottom: '1px solid #ccc'
            }}
          >
            <strong>{article.title}</strong> <br />
            <small>Company: {article.company}</small>
          </li>
        ))}
      </ul>
    </div>
  );
}

function ArticleDetail({ article, analysis }) {
  if (!article) return null;

  return (
    <div style={{ borderTop: '1px solid #ccc', paddingTop: 20 }}>
      <h2>Article Detail</h2>
      <h3>{article.title}</h3>
      <p><strong>Company:</strong> {article.company}</p>
      <p>
        <a href={article.url} target="_blank" rel="noreferrer">
          Read Full Article
        </a>
      </p>
      <h4>ESG Analysis</h4>
      <pre
        style={{
          whiteSpace: 'pre-wrap',
          background: '#f7f7f7',
          padding: 10,
          borderRadius: 5,
          maxHeight: 300,
          overflowY: 'auto'
        }}
      >
        {analysis || 'No analysis available'}
      </pre>
    </div>
  );
}

function App() {
  const [articles, setArticles] = useState([]);
  const [selectedArticle, setSelectedArticle] = useState(null);
  const [analysis, setAnalysis] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetch('/api/analyze_companies', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ companies: ['Temasek', 'Sembcorp'] }) // Thay danh sách công ty tùy ý
    })
      .then(res => res.json())
      .then(data => {
        const allArticles = [];
        data.forEach(companyData => {
          companyData.articles.forEach(article => {
            allArticles.push({
              ...article,
              company: companyData.company
            });
          });
        });
        setArticles(allArticles);
      })
      .catch(err => {
        console.error('Error fetching ESG data:', err);
      });
  }, []);

  const handleSelectArticle = (article) => {
    setSelectedArticle(article);
    setAnalysis(article.analysis || 'No analysis available');
  };

  return (
    <div style={{ maxWidth: 800, margin: 'auto', padding: 20 }}>
      <h1>ESG News Monitor</h1>
      <ArticleList articles={articles} onSelect={handleSelectArticle} />
      {selectedArticle && (
        <ArticleDetail article={selectedArticle} analysis={analysis} />
      )}
    </div>
  );
}

export default App;
