import React, { useState, useEffect } from 'react';
import ArticleList from './components/ArticleList';
import ArticleDetail from './components/ArticleDetail';

function App() {
  const [articles, setArticles] = useState([]);
  const [selectedArticle, setSelectedArticle] = useState(null);
  const [analysis, setAnalysis] = useState('');
  const [loading, setLoading] = useState(false);

  // Load articles từ backend khi app mount
  useEffect(() => {
    fetch('/api/articles')
      .then(res => res.json())
      .then(data => setArticles(data))
      .catch(err => console.error(err));
  }, []);

  const handleSelectArticle = (article) => {
    setSelectedArticle(article);
    setLoading(true);
    setAnalysis('');

    fetch('/api/analyze', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ title: article.title, content: article.content })
    })
      .then(res => res.json())
      .then(data => {
        setAnalysis(data.analysis);
        setLoading(false);
      })
      .catch(() => {
        setAnalysis('Failed to load analysis.');
        setLoading(false);
      });
  };

  return (
    <div style={{ maxWidth: 800, margin: 'auto', padding: 20 }}>
      <h1>ESG News Monitor</h1>
      <ArticleList articles={articles} onSelect={handleSelectArticle} />
      {selectedArticle && (
        <ArticleDetail
          article={selectedArticle}
          analysis={analysis}
          loading={loading}
        />
      )}
    </div>
  );
}

export default App;
