import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';

function ArticleDetail({ article, analysis, loading }) {
  return (
    <div
      style={{
        marginTop: 30,
        backgroundColor: '#e3f2fd', // lớp nền ngoài cùng
        padding: 15,
        borderRadius: 10,
        border: '1px solid #90caf9',
        fontFamily: 'Arial, sans-serif',
      }}
    >
      <div
        style={{
          backgroundColor: 'rgba(13, 71, 161, 0.12)', // lớp thứ 2, xanh đậm hơn
          padding: 20,
          borderRadius: 8,
          color: '#0d47a1',
          boxShadow: '0 2px 6px rgba(0,0,0,0.05)',
          backdropFilter: 'blur(2px)',
        }}
      >
        <h2>{article.title}</h2>
        <p><strong>Company:</strong> {article.company}</p>
        <a
          href={article.url}
          target="_blank"
          rel="noopener noreferrer"
          style={{
            color: '#1976d2',
            textDecoration: 'underline',
            display: 'inline-block',
            marginBottom: 15,
          }}
        >
          View Article
        </a>

        <h3 style={{ marginTop: 20 }}>Analysis</h3>
        {loading ? (
          <p>Loading analysis...</p>
        ) : (
          <div
            style={{
              backgroundColor: 'rgba(13, 71, 161, 0.2)', // lớp trong cùng
              padding: 15,
              borderRadius: 6,
              border: '1px solid #90caf9',
              lineHeight: 1.6,
              whiteSpace: 'pre-wrap',
              color: 'white',
              fontWeight: '500',
            }}
          >
            <ReactMarkdown remarkPlugins={[remarkGfm]}>
              {analysis || 'No analysis available'}
            </ReactMarkdown>
          </div>
        )}
      </div>
    </div>
  );
}

export default ArticleDetail;
